-- TYPE TEMPLOYE
create or replace type temploye as object( NUM_EMP Number(7),
                                NOM_EMP varchar2(30),
                                PRENOM_EMP varchar2(30),
                                ADRESSE_EMP varchar2(100),
                                TEL_EMP varchar2(10)
                                ) not final;
/

-- TYPE TMEDECIN
create or replace type tmedecin under temploye( NUM_MED number(7),
                                     SPECIALITE varchar2(40),
